import { App, PluginSettingTab, Setting } from "obsidian";
import BongoCatPlugin from "./main";
import { VIEW_TYPE_BONGO_CAT } from "view";
import { BongoCatView } from "view";

export interface BongoCatSettings {
	idleTimeout: number;
	deepIdleTimeout: number;
	catSize: number;
	imgIdleUp: string;
	imgIdleDown: string;
	imgLeftDown: string;
	imgRightDown: string;
}

export const DEFAULT_SETTINGS: BongoCatSettings = {
	idleTimeout: 2.0,
	deepIdleTimeout: 5.0,
	catSize: 300,
	imgIdleUp:
		"data:image/png;base64,",
	imgIdleDown:
		"data:image/png;base64,",
	imgLeftDown:
		"data:image/png;base64,",
	imgRightDown:
		"data:image/png;base64,",
};

export class BongoCatSettingTab extends PluginSettingTab {
	plugin: BongoCatPlugin;

	constructor(app: App, plugin: BongoCatPlugin) {
		super(app, plugin);
		this.plugin = plugin;
	}

	display(): void {
		const { containerEl } = this;
		containerEl.empty();

		// --- Configuracin de Tamao ---
		new Setting(containerEl)
			.setName("Cat size (px)")
			.setDesc("Adjust the width of the bongo cat.")
			.addSlider((slider) =>
				slider
					.setLimits(50, 600, 10)
					.setValue(this.plugin.settings.catSize)
					.setDynamicTooltip()
					.onChange(async (value) => {
						this.plugin.settings.catSize = value;
						await this.plugin.saveSettings();
						// Opcional: Avisar que requiere reabrir la vista para ver cambios grandes
						refreshView();
					}),
			);

		new Setting(containerEl)
			.setName("Reset to defaults")
			.setDesc(
				"Restore all settings to their original values. This will overwrite your custom images.",
			)
			.addButton((button) =>
				button
					.setButtonText("Reset settings")
					.setWarning() // Lo pone en rojo
					.onClick(async () => {
						// Restauramos el objeto de settings al estado inicial
						this.plugin.settings = Object.assign(
							{},
							DEFAULT_SETTINGS,
						);
						await this.plugin.saveSettings();
						// Forzamos el refresco visual de la pestaa de settings
						this.display();
					}),
			);

		const refreshView = () => {
			const leaves =
				this.app.workspace.getLeavesOfType(VIEW_TYPE_BONGO_CAT);
			leaves.forEach((leaf) => {
				if (leaf.view instanceof BongoCatView) {
					leaf.view.updateView();
				}
			});
		};

		// --- Configuracin de Sprites (Repetir para los 4) ---
		const createSpriteSetting = (
			name: string,
			desc: string,
			key: keyof BongoCatSettings,
		) => {
			new Setting(this.containerEl)
				.setName(name)
				.setDesc(desc)
				.addTextArea((text) => {
					text.setPlaceholder("Paste base64 string here...")
						.setValue(String(this.plugin.settings[key]))
						.onChange(async (value) => {
							const settings = this.plugin
								.settings as unknown as Record<
								string,
								string | number
							>;
							settings[key as string] = value;
							await this.plugin.saveSettings();
							refreshView();
						});

					// Configuramos las filas y columnas del rea de texto
					text.inputEl.rows = 4;
					text.inputEl.cols = 40;

					// CORRECCIN: Usamos setCssStyles en lugar de .style.width
					text.inputEl.setCssStyles({
						width: "100%",
					});
				});
		};

		createSpriteSetting(
			"Idle Up Sprite",
			"Image when paws are up.",
			"imgIdleUp",
		);
		createSpriteSetting(
			"Idle Down Sprite",
			"Image when paws are down.",
			"imgIdleDown",
		);
		createSpriteSetting(
			"Left Down Sprite",
			"Image when left paw is hitting.",
			"imgLeftDown",
		);
		createSpriteSetting(
			"Right Down Sprite",
			"Image when right paw is hitting.",
			"imgRightDown",
		);

		new Setting(containerEl)
			.setName("Idle timeout (seconds)")
			.setDesc("Time before the cat raises both paws (both-up).")
			.addText((text) =>
				text
					.setPlaceholder("1.0")
					.setValue(this.plugin.settings.idleTimeout.toString())
					.onChange(async (value) => {
						const num = parseFloat(value);
						if (!isNaN(num) && num >= 0) {
							this.plugin.settings.idleTimeout = num;
							await this.plugin.saveSettings();
						}
					}),
			);

		new Setting(containerEl)
			.setName("Deep idle timeout (seconds)")
			.setDesc(
				"Time after both-up before the cat rests both paws (both-down).",
			)
			.addText((text) =>
				text
					.setPlaceholder("10.0")
					.setValue(this.plugin.settings.deepIdleTimeout.toString())
					.onChange(async (value) => {
						const num = parseFloat(value);
						if (!isNaN(num) && num >= 0) {
							this.plugin.settings.deepIdleTimeout = num;
							await this.plugin.saveSettings();
						}
					}),
			);
	}
}
